const winston = require('winston');
function createNewLogger(loggerName="None") {
  const logger = new (winston.Logger)({
    transports: [
      new (winston.transports.Console)({
        colorize: true,
        timestamp: true,
        label: loggerName,
      }),
    ],
  });
  
  if (process.env.NODE_ENV !== 'production') {
    logger.level = 'debug';
  } else {
    logger.level = 'info';
  };

  return logger;
};
module.exports = createNewLogger;
