function printObject(obj, offset = 0) {
  const keys = Object.keys(obj);
  keys.map(key => {
    if (typeof obj[key] === 'object') {
      console.log(`${" ".repeat(offset)}${key}:`);
      printObject(obj[key], offset + 2);
      return;
    }
    console.log(`${" ".repeat(offset)}${key}: ${obj[key]}`);
  });
}

module.exports = printObject;
