const logger = require('logger.js')("Task Handler Module");


async function messageHandler(client, task) {
  const channel = client.channels.get(task.channel);
  console.log(task.owner);
  channel.send(`<@${task.owner}>! Bear has message for you. \`\`\`${task.args}\`\`\``);
}

async function autoMarketHandler(client, task) {

}

async function taskHandler(client, task) {
  switch (task.type) {
    case "message":
      messageHandler(client, task); 
      break;
    case "auto-market":
      autoMarketHandler(client, task);
      break;

    default:
      const channel = client.channels.get(task.channel);     
      channel.send("Invalid task type, please try again.");
  }
}

exports.messageHandler = messageHandler;
